mapper.select:
SELECT ADMIN_SN , ADMIN_ID , ADMIN_NM , USE_YN , REG_SN , MOD_SN , DATE_FORMAT(REG_DATE, '%Y-%m-%d %H:%i:%S') AS REG_DATE_STR , DATE_FORMAT(MOD_DATE, '%Y-%m-%d %H:%i:%S') AS MOD_DATE_STR FROM TC_ADMIN_MS WHERE 1=1
AND ADMIN_SN = #{adminSn}
AND ADMIN_ID LIKE CONCAT('%',#{adminId},'%')
AND ADMIN_NM LIKE CONCAT('%',#{adminNm},'%')
AND USE_YN = #{useYn}
ORDER BY ADMIN_SN DESC LIMIT #{skip} , #{pageSize}
