mapper.update:
UPDATE TC_ADMIN_MENU_LS
   SET MENU_SN = #{menuSn}

       ,SUPI_MENU_SN = #{supiMenuSn}
       ,MENU_NM = #{menuNm}
       ,MENU_URL = #{menuUrl}
       ,DEPT = #{dept}
       ,ORDR = #{ordr}
       ,USE_YN = #{useYn}
       ,DSP_YN = #{dspYn}
       ,REG_DATE = #{regDate}
       ,REG_SN = #{regSn}
       ,MOD_DATE = #{modDate}
       ,MOD_SN = #{modSn}
 WHERE 1=1
   AND MENU_SN = #{menuSn}

mapper.delete:
DELETE FROM TC_ADMIN_MENU_LS
 WHERE 1=1
   AND MENU_SN = #{menuSn}
