mapper.select:
SELECT
    *
  FROM TC_ADMIN_MS
 WHERE 1=1
 AND ADMIN_SN = #{adminSn}
 AND ADMIN_ID = #{adminId}
 AND ADMIN_PW = #{adminPw}
 AND ADMIN_NM = #{adminNm}
 AND USE_YN = #{useYn}
 AND REG_DATE = #{regDate}
 AND REG_SN = #{regSn}
 AND MOD_DATE = #{modDate}
 AND MOD_SN = #{modSn}
 ORDER BY ADMIN_SN DESC
 LIMIT #{skip} , #{pageSize}

mapper.update:
UPDATE TC_ADMIN_MS
   SET ADMIN_SN = #{adminSn}

       ,ADMIN_ID = #{adminId}
       ,ADMIN_PW = #{adminPw}
       ,ADMIN_NM = #{adminNm}
       ,USE_YN = #{useYn}
       ,REG_DATE = #{regDate}
       ,REG_SN = #{regSn}
       ,MOD_DATE = #{modDate}
       ,MOD_SN = #{modSn}
 WHERE 1=1
   AND ADMIN_SN = #{adminSn}

mapper.delete:
DELETE FROM TC_ADMIN_MS
 WHERE 1=1
   AND ADMIN_SN = #{adminSn}
