mapper.update:
UPDATE TC_ADMIN_MS
   SET adminSn= #{adminSn}, adminId= #{adminId}, adminPw= #{adminPw}, adminNm= #{adminNm}, useYn= #{useYn}, regDate= #{regDate}, regSn= #{regSn}, modDate= #{modDate}, modSn= #{modSn}
 WHERE 1=1
   AND ADMIN_SN = #{adminSn}

mapper.delete:
DELETE FROM TC_ADMIN_MS
 WHERE 1=1
   AND ADMIN_SN = #{adminSn}
