mapper.update:
UPDATE TC_ADMIN_MS
   SET ADMIN_SN = #{adminSn}

       ,ADMIN_ID = #{adminId}
       ,ADMIN_PW = #{adminPw}
       ,ADMIN_NM = #{adminNm}
       ,USE_YN = #{useYn}
       ,REG_DATE = #{regDate}
       ,REG_SN = #{regSn}
       ,MOD_DATE = #{modDate}
       ,MOD_SN = #{modSn}
 WHERE 1=1
   AND ADMIN_SN = #{adminSn}

mapper.delete:
DELETE FROM TC_ADMIN_MS
 WHERE 1=1
   AND ADMIN_SN = #{adminSn}
