mapper.update:
UPDATE TC_AUTH_MS
   SET AUTH_SN = #{authSn}

       ,AUTH_NM = #{authNm}
       ,USE_YN = #{useYn}
       ,REG_DATE = #{regDate}
       ,REG_SN = #{regSn}
       ,MOD_DATE = #{modDate}
       ,MOD_SN = #{modSn}
 WHERE 1=1
   AND AUTH_SN = #{authSn}

mapper.delete:
DELETE FROM TC_AUTH_MS
 WHERE 1=1
   AND AUTH_SN = #{authSn}
